package First;

public class ExplicitCast {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 100;
		byte b = (byte) i;

		int x=97;
		char ch = (char)x;
		System.out.println(x);
		
		int y= 130;
		byte z=(byte)y;
		System.out.println(z);
		
		int m=612;
		byte n=(byte)m;
		char o=(char)n;
		System.out.println(o);
	}

}
