package mathdemo;

public class MathDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 16, n2 = 2;
		System.out.println("max of 2 numbers: "+Math.max(n1, n2));
		System.out.println("min of 2 numbers: "+Math.min(n1, n2));
		System.out.println("sqrt of n1: "+Math.sqrt(n1));
		System.out.println("Power of n1: "+Math.pow(n1, n2));
	}

}
